        if (mappings[blocks[i]].type === 'servo'){
            var behaviour = flow.get(blocks[i]) || 0;
            if (behaviour === 0){
                continue;
            }
            // if continuous emotion
            if (block_continuous === true) {
                var target_angle = parseInt(behaviour.happy_angle);
                var opposite_angle = parseInt(behaviour.sad_angle);
                var emotion_level = happy;
                if ( sad > happy ) {
                    target_angle = parseInt(behaviour.sad_angle);
                    opposite_angle = parseInt(behaviour.happy_angle);
                    emotion_level = sad;
                }
                if (target_angle >= opposite_angle){
                    var halfrange = Math.round((target_angle - opposite_angle) / 2);
                    var midpoint = opposite_angle + halfrange;
                    var angle = Math.round(target_angle - ((1-(emotion_level/100)) * halfrange));
                } else {
                    var halfrange = Math.round((opposite_angle - target_angle) / 2);
                    var midpoint = target_angle + halfrange;
                    var angle = Math.round(target_angle + ((1-(emotion_level/100)) * halfrange));
                }

            } else { // else discrete emotion
                var angle = parseInt(behaviour.happy_angle);
                if (sad > happy){
                    angle = parseInt(behaviour.sad_angle);
                }
            }
            output_msg.push({topic: blocks[i], payload: 'goto ' + angle.toString()})
        }
        else if (mappings[blocks[i]].type === 'pixel'){ // else LED
            var behaviour = flow.get(blocks[i]) || 0;
